/*export const insertar_articulo = async (datos) => {
  try {
    const articulo = await dbPool.connect();
    const { ean_art, nom_art, consec_marca, consec_categ, descrip_art, unid_med,  iva} = datos;
    
    // Consulta de inserción
    const result = await articulo.query("INSERT INTO tab_articulo (ean_art, nom_art, consec_marca, consec_categ, descrip_art, unid_med, iva) VALUES ($1, $2, $3, $4, $5, $6, $7)", ['000000021', 'Martillo', 1 , 1, 'martillo mango madera', 'N/A', 19]);

    articulo.release();
    return result;
  } catch (error) {
    console.error('Error al insertar el artículo:', error);
    throw error;
  }
};*/




export const insertar_articulo = async (datos) => {
  try {
    const articulo = await dbPool.connect();
    const { ean_art, nom_art, consec_marca, consec_categ, descrip_art, unid_med, iva, fec_vence } = datos;
    
    // Consulta de inserción utilizando la función insert_articulo de la base de datos
    const result = await articulo.query(
      "SELECT insert_articulo($1, $2, $3, $4, $5, $6, $7, $8)",
      ['00000002','Espatula',1,1,'espatula 3','N/A',19,'2023-11-21']
    );

    articulo.release();
    return result;
  } catch (error) {
    console.error('Error al insertar el artículo:', error);
    throw error;
  }
};

